changeSet.sql:
ALTER TABLE diamond
    ADD stone_grams numeric GENERATED ALWAYS AS (carat_to_gram(stone_carat)) STORED;
ALTER TABLE diamond
    ADD grams_left numeric GENERATED ALWAYS AS (carat_to_gram(stone_carat) * quantity) STORED;

ALTER TABLE gemstone
    ADD total_weight_grams numeric GENERATED ALWAYS AS (carat_to_gram(total_weight)) STORED;
